import React from 'react';
import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import $ from 'jquery';
import Request from '../services/Request';
import './css/Signup.css'
import OtpDialog from './util/OtpDialog';

function Signup() {

    const processing = (el, text) => {
        let icon = '<i class="fas fa-sync-alt fa-spin mr-5"></i>';
        el.innerHTML = icon + text;
    }

    const postData = (url, data, req) => {
        $.ajax({
            url,
            type: 'POST',
            data,
            xhrFields: {
                withCredentials: true
            },
            success: (msg) => {

            },
            complete: (res) => {
                try {
                    res = JSON.parse(res.responseText);
                }catch(e) {
                    alert('Something went wrong!');
                }
                if(req == 'email-verification')
                    renderOtpDialog(res);
                else if(req == 'verify-otp')
                    otpResponse(res);
            }
        });
    }

    const adminRegistration = (e) => {
        processing(document.getElementById('admin-registration-btn'), 'Processing');
        e.preventDefault();
        let formData = $('#admin-signup-form').serialize();
        console.log(formData);
        let url = 'http://localhost:8080/QuizWit/register-admin';
        Request.post(url, formData).then((res) => {
            populateAdminResponse(res);
        }).catch((e) => {
            // flash message
        })
        return false;
    }

    const populateAdminResponse = (res) => {
        document.getElementById('admin-registration-btn').innerText = 'Register';
        let responseBlock = document.getElementById('admin-signup-form').getElementsByClassName('response');

        if(res.success) {
            adminRegistrationReset();
            alert('login successful');
        }
        else {
            let log = res.errorLog;
            let icon = '<i class="fas fa-exclamation-circle mr-5"></i>';
            responseBlock[0].innerHTML = (log.firstName ? icon + log.firstName : '');

            responseBlock[2].innerHTML = (log.gender ? icon + log.gender : '');
            responseBlock[4].innerHTML = (log.email ? icon + log.email: '');
            responseBlock[6].innerHTML = (log.dateOfBirth ? icon + log.dateOfBirth: '' );
            responseBlock[7].innerHTML = (log.password ? icon + log.password: '');
            responseBlock[8].innerHTML = (log.confirmPassword ? icon + log.confirmPassword: '' );

        }

    }

    const adminRegistrationReset = () => {
        document.getElementById('admin-signup-form').reset();
    }

    const adminEmailVerification = () => {
        let verifyBtn = document.getElementById('admin-email-verify-btn');
        processing(verifyBtn, 'Sending OTP');
        localStorage.setItem('verificationEmail', 'admin-email');
        emailVerification();
    }

    const emailVerification = () => {
        let emailElementId = localStorage.getItem('verificationEmail');
        let email = document.getElementById(emailElementId).value;
        let data = 'email=' + email;
        let url = 'http://localhost:8080/QuizWit/email-verification';
        postData(url, data, 'email-verification');
        Request.post(url, data).then((res) => {
            renderOtpDialog(res);
        }).catch((e) => {
            // flash message
        })
        return false;
    }

    const [otpDialogToggle, setOtpDialogToggle] = useState(0);

    const renderOtpDialog = (res) => {
        console.log('render otp dialog function');
        console.log(res);
        let verifyBtn = document.getElementById('admin-email-verify-btn');
        verifyBtn.innerText = 'Verify';
        if(res.success) {
            setOtpDialogToggle(!otpDialogToggle);
            toggleDivHorizontal('otp-dialog', otpDialogToggle, 344, 0);
            let otpDialog = document.getElementById('otp-dialog');
            let inputs = otpDialog.getElementsByTagName('input');
            inputs[0].focus();
            console.log('dialog open');
        }
        else {
            let icon = '<i class="fas fa-exclamation-circle mr-5"></i>';
            document.getElementById('admin-email-response').innerHTML = icon + res.error;
        }
    }

    const toggleDivHorizontal = (id, t, openWidth, closeWidth) => {
        let el = document.getElementById(id);
        let width = t ? openWidth : closeWidth;
        el.style.transition = 'all 800ms';
        el.style.width = width + 'px';
    }

    const matchOtp = () => {
        let otpDialog = document.getElementById('otp-dialog');
        let icon = '<i class="fas fa-exclamation-circle mr-5"></i>';
        let type = 'emailOtp';
        let inputs = otpDialog.getElementsByTagName('input');
        let otp = '';
        for(let i=0; i<inputs.length; i++) {
            if(inputs[i].value != '')
                otp += inputs[i].value;
            else {
                let responseBlock = otpDialog.getElementsByClassName('response')[0];
                responseBlock.innerHTML = icon + 'OTP required';
                return;
            }
        }
        let data = 'type=' + type + '&' + 'otp=' + otp;
        console.log(data);
        let url = 'http://localhost:8080/QuizWit/verify-otp';
        postData(url, data, 'verify-otp');
    }

