class Animation {

    toggleDivHorizontal(id, t, open, closeWidth) {
        let el = document.getElementById(id);
        let width = t ? open : closeWidth;
        el.style.transition = 'all 800ms';
        el.style.width = width + 'px';
    }

    toggleDivVertical(id, t, open, closeWidth) {
        let el = document.getElementById(id);
        let width = t ? open : closeWidth;
        el.style.transition = 'all 800ms';
        el.style.width = width + 'px';
    }
}

export default new Animation();
