import React from 'react';
import { Link } from 'react-router-dom';
import $ from 'jquery';

import './css/Signup.css'

function Signup() {

    const postData = (url, data) => {
        $.ajax({
            url,
            type: 'POST',
            data,
            success: (msg) => {

            },
            complete: (res) => {
                try {
                    res = JSON.parse(res.responseText);
                }catch(e) {
                    alert(e);
                }

                populateAdminResponse(res);
            }
        });
    }

    const adminRegistration = (e) => {
        e.preventDefault();
        let formData = $('#admin-signup-form').serialize();
        console.log(formData);
        let url = 'http://localhost:8080/QuizWit/register-admin';
        postData(url, formData);
        return false;
    }

    const populateAdminResponse = (res) => {
        let responseBlock = document.getElementById('admin-signup-form').getElementsByClassName('response');

        if(res.success) {
            alert('login successful');
        }
        else {
            let log = res.errorLog;
            let icon = "<i class='fas fa-exclamation-circle mr-5'></i>";
            responseBlock[0].innerHTML = (log.firstName ? icon + log.firstName : '');

            responseBlock[2].innerHTML = (log.gender ? icon + log.gender : '');
            responseBlock[4].innerHTML = (log.email ? icon + log.email: '');
            responseBlock[6].innerHTML = (log.dateOfBirth ? icon + log.dateOfBirth: '' );
            responseBlock[7].innerHTML = (log.password ? icon + log.password: '');
            responseBlock[8].innerHTML = (log.confirmPassword ? icon + log.confirmPassword: '' );

        }

    }

    const adminRegistrationReset = () => {
        document.getElementById('admin-signup-form').reset();
    }


    return (
        <div className='signup-cover'>
            <form id='admin-signup-form' className='form sign-up-form' onSubmit={adminRegistration}>
                <div className='header'>Admin Registration</div>
                <div className="input-block">
                    <div className="input-container-bb">
                        <input type="text" name="firstName" />
                        <label>First Name</label>
                        <div className="response"></div>
                    </div>
                    <div className="input-container-bb">
                        <input type="text" name="lastName" />
                        <label>Last Name</label>
                        <div className="response"></div>
                    </div>
                </div>
                <div className="input-block">
                    <div className='customized-radio-sticky'>
                        <label>Gender</label>
                        <div>
                            <label>
                                <input type='radio' name='gender' value='1' />
                                <span>Male</span>
                            </label>
                            <label>
                                <input type='radio' name='gender' value='2' />
                                <span>Female</span>
                            </label>
                            <label>
                                <input type='radio' name='gender' value='3' />
                                <span>Others</span>
                            </label>
                        </div>
                        <div className='response'></div>
                    </div>
                    <div className="input-container-bb">
                        <input type="number" name="contact" />
                        <label>Contact</label>
                        <div className="response"></div>
                    </div>
                </div>
                <div className="input-block ai-s">
                    <div className="input-container-bb">
                        <input type="email" name="email" />
                        <label>Email</label>
                        <div className="response"></div>
                    </div>
                    <label className='btn btn-success btn-small ml-10'>Verify</label>
                </div>
                <div className="input-block">
                    <div className="input-container-bb">
                        <input type="text" name="institution" />
                        <label>Institution</label>
                        <div className="response"></div>
                    </div>
                    <div className="input-container-bb">
                        <input type="date" name="dateOfBirth" />
                        <label>Date of Birth</label>
                        <div className="response"></div>
                    </div>
                </div>
                <div className="input-block">
                    <div className="input-container-bb">
                        <input type="password" name="password" />
                        <label>Password</label>
                        <div className="response"></div>
                    </div>
                    <div className="input-container-bb">
                        <input type="password" name="confirmPassword" />
                        <label>Confirm Password</label>
                        <div className="response"></div>
                    </div>
                </div>
                <div className="flex-row jc-sb">
                    <div className="btn btn-fade btn-small" onClick={adminRegistrationReset}>Reset</div>
                    <button className="btn btn-secondary btn-small">Register</button>
                </div>
                <div className='footer'>
                    Already have an account ? <Link className='anchor-color-primary' to='/sign-in'>Sign In</Link>
                </div>
            </form>
        </div>
    )
}

export default Signup
