import React from 'react';
import { useEffect } from 'react';
import './css/Faq.css';

function Faq() {
    useEffect(() => {
        document.getElementById('faq-btn').addEventListener('click', () => {
            document.getElementsByClassName('faq')[0].scrollIntoView();
        });

        let questions = document.getElementsByClassName('question');
        let answers = document.getElementsByClassName('answer');
        let iconContainer = document.getElementsByClassName('icon-container');
        for(let i=0; i<questions.length; i++) {
            questions[i].addEventListener('click', () => {
                let v = answers[i].style.display;
                v = v == 'block' ? 'none' : 'block';
                answers[i].style.display = v;
            });
        }

    }, []);
    return (
        <div className='faq'>
            <h1>Frequently Asked Questions</h1>
            <div className='container'>
                <div className='question'>
                    <div>
                        What is QuizWit?
                    </div>
                    <div className='icon-container'>
                        <i className='fas fa-plus'></i>
                    </div>
                </div>
                <div className='answer'>
                    <p>QuizWit is a online exam web application that enables you to tackle your weak points in exams with the help of analytics.

                    </p>
                </div>
            </div>
            <div className='container'>
                <div className='question'>
                    <div>
                        How much QuizWit cost?
                    </div>
                    <div className='icon-container'>
                        <i className='fas fa-plus'></i>
                    </div>
                </div>
                <div className='answer'>
                    <p>Netflix is a streaming service that offers a wide variety of award-winning TV shows, movies, anime, documentaries and more – on thousands of internet-connected devices.

    You can watch as much as you want, whenever you want, without a single ad – all for one low monthly price. There's always something new to discover, and new TV shows and movies are added every week!</p>
                </div>
            </div>
            <div className='container'>
                <div className='question'>
                    <div>
                        Features?
                    </div>
                    <div className='icon-container'>
                        <i className='fas fa-plus'></i>
                    </div>
                </div>
                <div className='answer'>
                    <p className='flex-row'>
                        <div className='flex-full'>
                            <h4>Admin</h4>
                            <ul>
                                <li>Navigation Control</li>
                                <li></li>
                                <li></li>
                                <li></li>
                            </ul>
                        </div>
                        <div className='flex-full'>
                            <h4>Student</h4>
                            <ul>
                                <li></li>
                                <li></li>
                                <li></li>
                                <li></li>
                                <li></li>
                            </ul>
                        </div>
                    </p>
                </div>
            </div>
        </div>
    );
}

export default Faq;
